property.value:
jQuery('body')
    .subscribe('SECTION_ITEM_ADDED', function(data){
        var link = '<a href="inquiry?methodToCall=start&viewId=SingleTerm-InquiryView&term_atp_id=' + data.atpId.replace(/-/g,'.') + '">Added</a>';
        jQuery('#section_' + data.InstituteCode + '_' + data.atpId + '_' + data.SectionCode).attr('title', data.SectionCode + ' is added. To delete or move, go to ' + data.termName + ' Plan.').unbind('click');
        fnDisplayMessage(link, 'fl-font-size-80', 'section_' + data.InstituteCode + '_' + data.atpId + '_' + data.SectionCode, false);
        if (data.PrimarySectionCode != null) {
            jQuery('#section_' + data.InstituteCode + '_' + data.atpId + '_' + data.PrimarySectionCode).attr('title', data.PrimarySectionCode + ' is added. To delete or move, go to ' + data.termName + ' Plan.').unbind('click');
            fnDisplayMessage(link, 'fl-font-size-80', 'section_' + data.InstituteCode + '_' + data.atpId + '_' + data.PrimarySectionCode, false);
        }
    });

jQuery('#course_details_actions')
    .subscribe('PLAN_ITEM_ADDED', function(data){
        if (data.planItemType === 'wishlist') {
            fnDisplayMessage(data.message, data.cssClass, data.courseDetails.courseId+'_addSavedCourse', true, null, false, '@{dataObject.courseSummaryDetails.courseId}_bookmarked');
        }
    })
    .subscribe('PLAN_ITEM_DELETED', function(data){
        if (data.planItemType === 'wishlist' && data.courseDetails.courseId === '@{dataObject.courseSummaryDetails.courseId}') {
            fnRestoreDetailsAddButton(data.courseDetails.courseId);
        }
    });

jQuery('#@{dataObject.courseSummaryDetails.courseId}_addSavedCourse')
    .subscribe('PLAN_ITEM_ADDED', function(data){
        if (data.planItemType === 'wishlist') {
            fnDisplayMessage(data.message, data.cssClass, data.courseDetails.courseId+'_addSavedCourse', true, null, false, '@{dataObject.courseSummaryDetails.courseId}_bookmarked');
        }
    })
    .subscribe('PLAN_ITEM_DELETED', function(data){
        if (data.planItemType === 'wishlist' && data.courseDetails.courseId === '@{dataObject.courseSummaryDetails.courseId}') {
            fnRestoreDetailsAddButton(data.courseDetails.courseId);
        }
    });

jQuery('#course_details_section_datatable_info').after('<div class="dataTables_filter"><label for="section_filter">Show</label><select id="section_filter" name="section_filter"><option value="all">All Sections</option><option value="open">Open Sections only</option></select></div>');
jQuery('.dataTables_filter select').change(function() {
    var oTable = jQuery('#course_details_section_datatable').dataTable();
    switch (jQuery(this).val()) {
        case 'all':
            oTable.fnFilter('', 6, true);
            break;
        case 'open':
            oTable.fnFilter('^((?!closed).)*$', 6, true);
            break;
        case 'closed':
            oTable.fnFilter('closed', 6, true);
            break;
    }
});
