jQuery('form input, form textarea').keyup(function(event) {
    var empty = false;
    var code = (event.keyCode ? event.keyCode : event.which);
    jQuery('form .myplan-required').each(function() {
        if (jQuery(this).val().replace(/\n/g, '').length == 0) {
            empty = true;
        }
    });
    if (code == 13 && event.target.tagName.toLowerCase() == 'input' && event.target.type == 'text' && empty) {
        jQuery('button#cancelMessage').click();
    }
    jQuery('button#leaveMessageButton').attr('disabled', empty);
});

