var retrieveData = {action:'plan', viewId:'PlannedCourse-FormView', methodToCall:'startAddDialog', courseId:'@{dataObject.courseSummaryDetails.courseId}',pageId:'add_dialog_page'};
var popupStyle = {width:'300px', height:'16px'};
var popupOptions = {tail:{hidden:true}, align:'top', close:true};
var pageId = 'add_dialog_page.@{dataObject.courseSummaryDetails.courseIdXmlSafe}';
openPopup(pageId, retrieveData, 'plan', popupStyle, popupOptions, e);

jQuery('#@{dataObject.courseSummaryDetails.courseIdXmlSafe}_addSavedCourse')
    .on('PLAN_ITEM_ADDED', function(event, data){
        if (data.planItemType === 'wishlist') {
            fnDisplayMessage(data.message, data.cssClass, data.courseDetails.courseId+'_addSavedCourse', true, null, false, '@{dataObject.courseSummaryDetails.courseId}_bookmarked');
        }
    })
    .on('PLAN_ITEM_DELETED', function(event, data){
        if (data.planItemType === 'wishlist' && data.courseDetails.courseId === '@{dataObject.courseSummaryDetails.courseId}') {
            fnRestoreDetailsAddButton(data.courseDetails.courseId);
        }
    });

